import { createSlice } from "@reduxjs/toolkit";

const initialCounterState = {
  cartIsShown: false,
  notification: null
};

const uiSlice = createSlice({
  name: "ui",
  initialState: initialCounterState,
  reducers: {
    toggle(state) {
      state.cartIsShown = !state.cartIsShown;
    },
    showN
  },
});

export default uiSlice;
