import { useEffect } from "react";
import { useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";

function App() {
  const cartIsShown = useSelector((state) => state.ui.cartIsShown);
  const cart = useSelector((state) => state.cart);

  const abc = async () => {
    const responce = await fetch("https://redux-with-backend-default-rtdb.firebaseio.com/cart.json", {
      // method: "POST", //Add the http request in previous http request
      method: 'PUT', //Overwrite the previous http request
      body: JSON.stringify(cart),
    });
  };

  useEffect(() => {
    abc();
  }, [cart, abc]);
  return (
    <Layout>
      {cartIsShown && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
