import {createSlice} from '@reduxjs/toolkit';

const initialCounterState = {
    cartIsShown : false
};

const uiSlice = createSlice({
    name:'ui',
    initialState: initialCounterState,
    reducers: {
        toggle(state) {
            state.cartIsShown = !state.cartIsShown
        },
    }
});

export default cartShownSlice;
