import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { uiActions } from "./store/index";

function App() {
  const cartIsShown = useSelector((state) => state.ui.cartIsShown);
  const cart = useSelector((state) => state.cart);

  const dispatch = useDispatch();

  const sendingData = {
    status: "pending",
    title: "Sending...",
    message: "Sending Cart Data To The Server!!!",
  };

  const sendData = {
    status: "success",
    title: "Send...",
    message: "Send Cart Data To The Server Successfully!!!",
  };

  const error = {
    status: "error",
    title: "Error",
    message: "An ERROR Occured!!!",
  };

  useEffect(() => {
    const sentCartData = async () => {
      try {
        dispatch(uiActions.showNotification(sendingData));
        const response = await fetch(
          "https://redux-with-backend-default-rtdb.firebaseio.com/cart.json",
          {
            // method: "POST", //Add the http request in previous http request
            method: "PUT", //Overwrite the previous http request
            body: JSON.stringify(cart),
          }
        );

        if (!response.ok) {
          throw new Error("Sending cart data failed.");
        }

        dispatch(uiActions.showNotification(sendData));
      } catch (error) {
        dispatch(uiActions.showNotification(error));
      }
    };

    sentCartData();
  }, [cart, dispatch, sendData, sendingData]);
  return (
    <Layout>
      {cartIsShown && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
