import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = () => {
  const [enterTitle, setEnterTitle] = useState("");
  const [enterAmount, setEnterAmount] = useState("");
  const [enterDate, setEnterDate] = useState("");

  //Can be done using single state and objects also
  // const [userInput, setUserInput] = useState({
  //     enterTitle: '',
  //     enterAmount: '',
  //     enterDate: ''
  // });

  const titleChangeHandler = (event) => {
    setEnterTitle(event.target.value);
    //When single state is used
    // setUserInput((prevState) => {    //it takes an object, pulls out all the key value pairs and adds them to new object
    //     return{
    //         ...prevState,
    //         enterTitle : event.target.value
    //     }
    // });
  };

  const amountChangeHandler = (event) => {
    setEnterAmount(event.target.value); //event.target.value is always in string form
    //When single state is used
    // setUserInput((prevState) => {    //it takes an object, pulls out all the key value pairs and adds them to new object
    //     return{
    //         ...prevState,
    //         enterTitle : event.target.value
    //     }
    // });
  };

  const dateChangeHandler = (event) => {
    setEnterDate(event.target.value);
    //When single state is used
    // setUserInput((prevState) => {    //it takes an object, pulls out all the key value pairs and adds them to new object
    //     return{
    //         ...prevState,
    //         enterTitle : event.target.value
    //     }
    // });
  };

  return (
    <from onSubmit={submit}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="100"
            step="100"
            onChange={amountChangeHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2021-01-01"
            max="2025-12-31"
            onChange={dateChangeHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </from>
  );
};

export default ExpenseForm;
