import React from "react";
import Card from "./Card";
import Button from "../Users/Button";
import styles from "./ErrorModal.module.css";

const Backdorp = (props) => {
  return <div onClick={props.onConfirm} className={styles.backdrop} />;
};

const Modal = (props) => {
  return (
    <Card className={styles.modal}>
      <header className={styles.header}>
        <h2>{props.title}</h2>
      </header>
      <div className={styles.content}>
        <p>{props.message}</p>
      </div>
      <footer className={styles.actions}>
        <Button onClick={props.onConfirm}>Okay</Button>
      </footer>
    </Card>
  );
};

const ErrorModal = (props) => {
  return (
    <>
      <Backdorp onConfirm={props.onConfirm} />
      <Modal title={props.title} message={props.message} onClick={props.onConfirm} />
    </>
  );
};

export default ErrorModal;
