import { useState } from "react";

const BasicForm = (props) => {
  const [enteredFirstName, setEnteredFirstName] = useState("");
  const [firstNameIsTouched, setFirstNameIsTouched] = useState(false);

  const firstNameIsValid = enteredFirstName.trim() !== '';
  const firstNameIsNotValid = !firstNameIsValid && firstNameIsTouched

  const firstNameChangeHandler = (event) => {
    setEnteredFirstName(event.target.value);
    setFirstNameIsTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();
    console.log(enteredFirstName);
    setEnteredFirstName("");
  };
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameChangeHandler}
            value={enteredFirstName}
            onBlur={firstNameChangeHandler}
          />
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
