import { useState } from "react";
const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);
  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
    setEnteredNameIsValid(true);
    setEnteredNameIsTouched
  };
  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (enteredName.trim() === "") {
      setEnteredNameIsValid(false)
      return;
    }
    setEnteredNameIsValid(true); 
    console.log(enteredName);
    setEnteredName("");
  };

  const nameInputClasses = enteredNameIsValid && enteredNameIsTouched ? "form-control" : "form-control invalid";
  return (
    <form onSubmit={formSubmissionHandler}>
    <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          value={enteredName}
        />
        {!enteredNameIsValid && enteredNameIsTouched && <p className="error-text">Please enter your name.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
