import { useState } from "react";
import useInputs from "../hooks/use-inputs";

const BasicForm = (props) => {
  const [enteredFirstName, setEnteredFirstName] = useState("");
  const [firstNameIsTouched, setFirstNameIsTouched] = useState(false);

  const firstNameIsValid = enteredFirstName.trim() !== "";
  const firstNameIsNotValid = !firstNameIsValid && firstNameIsTouched;

  const firstNameChangeHandler = (event) => {
    setEnteredFirstName(event.target.value);
    setFirstNameIsTouched(true);
  };

  const { enteredValue, ValueIsTouched, valueIsValid, ValueIsNotValid } =
    useInputs();

  let formIsValid = false;

  if (firstNameIsValid) {
    formIsValid = true;
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if (!firstNameIsValid) {
      return;
    }

    console.log(enteredFirstName);
    setFirstNameIsTouched(false);
    setEnteredFirstName("");
  };

  const firstNameInputClass = firstNameIsNotValid
    ? "form-control invalid"
    : "form-control";
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className={firstNameInputClass}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameChangeHandler}
            value={enteredFirstName}
            onBlur={firstNameChangeHandler}
          />
          {firstNameIsNotValid && (
            <p className="error-text">Enter First Name.</p>
          )}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
